package javacode;
import java.util.*;
public class TableForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);
		System.out.println("Enter the Number");
		int X = sc.nextInt();
		for(int i=1;i<=10;i++)
		{
		 System.out.println(X*i);
		}
		
	}

}
